DROP TABLE IF EXISTS `Termos`;

CREATE TABLE `Termos` (
  `termoid` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `grupo` varchar(50) COLLATE latin1_general_ci NOT NULL,
  `palavra` varchar(150) COLLATE latin1_general_ci NOT NULL,
  `traducao` varchar(150) COLLATE latin1_general_ci NOT NULL,
  `exemplo` varchar(255) COLLATE latin1_general_ci NOT NULL,
  `tipo` varchar(150) COLLATE latin1_general_ci DEFAULT NULL,
  `unidade` varchar(20) COLLATE latin1_general_ci DEFAULT NULL,
  PRIMARY KEY (`termoid`)
) ENGINE=MyISAM DEFAULT;

